create table terms (
	ID bigint not null auto_increment,
	Json varchar(2500),
	Language varchar(5),
	Contract_ID bigint not null,
    constraint PK_Terms
		primary key (ID),
	constraint FK_Terms_Contract
		foreign key (Contract_ID)
		references contract(ID)
);

insert into terms
	(Language, Contract_ID, Json)
	select 'en-US', id, '{"Text": "The Don''t fly Money system, below referred as System, and the person who is registered at the system, below referred as User, agree with the described terms.", "Items":[{"Text": "The System has no links with any financial institutions.","Items": [{"Text": "System data is only inserted by the User;"},{"Text": "Moves made at the System do not change bank account data or any other external resource;"},{"Text": "Moves at external resource do not change System data, these being only editable by the User by the System;"}]},{"Text": "System data and decisions made based on this data are sole responsibility of the User."},{"Text": "Communication vehicles from System to User are:","Items": [{"Text": "E-mail informed by User on the registration at the System, which is only contacted by the addresses {{noreply_address}} - just for automatized alerts - and {{support_address}} - for support;"},{"Text": "Web system, registered at site {{site_address}};"},{"Text": "Android app {{android_address}}, installed by Play Store."}]},{"Text": "The User agrees to keep the registered e-mail updated."},{"Text": "Any law violations done through the System are sole responsibility of the User."},{"Text": "These terms can be changed any time in the future.","Items": [{"Text": "If the terms change, the User will receive a notice by e-mail and it will have 30 thirty day to accept the new terms;"},{"Text": "If the period expire and the User do not accept the new terms, the access to the System will be interrupted and the User will receive a notice by e-mail;"},{"Text": "Passed 90 days of the first notice, if the new terms are not accepted, User data will be permanently removed from the System."}]}]}'
		from contract
		where version = '003000000000'
	union all
	select 'en-US', id, '{"Text": "The Don''t fly Money system, below referred as System, and the person who is registered at the system, below referred as User, agree with the described terms.", "Items":[{"Text": "The System has no links with any financial institutions.","Items": [{"Text": "System data is only inserted by the User;"},{"Text": "Moves made at the System do not change bank account data or any other external resource;"},{"Text": "Moves at external resource do not change System data, these being only editable by the User by the System;"}]},{"Text": "System data and decisions made based on this data are sole responsibility of the User."},{"Text": "Communication vehicles from System to User are:","Items": [{"Text": "E-mail informed by User on the registration at the System, which is only contacted by the addresses {{noreply_address}} - just for automatized alerts - and {{support_address}} - for support;"},{"Text": "Web system, registered at site {{site_address}};"},{"Text": "Android app {{android_address}}, installed by Play Store."}]},{"Text": "The System source code - instructions to computer which rule the System - is hosted at a web address.","Items": [{"Text": "The address is public: {{github_address}}"},{"Text": "The source code is OPEN and ANY person using uncensored internet access can see it and suggest changes."}]},{"Text": "The User agrees to keep the registered e-mail updated."},{"Text": "Any law violations done through the System are sole responsibility of the User."},{"Text": "These terms can be changed any time in the future.","Items": [{"Text": "If the terms change, the User will receive a notice by e-mail and it will have 30 thirty day to accept the new terms;"},{"Text": "If the period expire and the User do not accept the new terms, the access to the System will be interrupted and the User will receive a notice by e-mail;"},{"Text": "Passed 90 days of the first notice, if the new terms are not accepted, User data will be permanently removed from the System."}]}]}'
		from contract
		where version = '004000000000'
	union all
	select 'pt-BR', id, '{"Text": "O sistema Don''t fly Money, abaixo constando como Sistema, e a pessoa que registrou-se no sistema, abaixo constando como Usufrente, concordam com os termos descritos.", "Items":[{"Text": "O Sistema não possui quaisquer vínculos com instituições financeiras.","Items": [{"Text": "Dados apresentados no Sistema são inseridos apenas por Usufrente;"},{"Text": "Movimentações feitas em Sistema não alteram dados em contas bancárias ou quaisquer outros meios externos;"},{"Text": "Movimentações em meios externos não afetam os dados de Sistema, sendo estes apenas alteráveis por Usufrente via Sistema;"}]},{"Text": "Dados apresentados em Sistema e decisões tomadas com base nestes dados são de total responsabilidade de Usufrente."},{"Text": "Os meios de comunicação de Sistema com Usufrente são:","Items": [{"Text": "E-mail cadastrado por Usufrente ao se registrar em Sistema, cujo contato é feito somente pelos endereços {{noreply_address}} - apenas para alertas automatizados - e {{support_address}} - para suporte;"},{"Text": "Sistema web, registrado no domínio {{site_address}};"},{"Text": "Aplicativo para dispositivos android {{android_address}}, instalação via Play Store."}]},{"Text": "Usufruente se compromete a manter o e-mail cadastrado atualizado."},{"Text": "Quaisquer atos que violem leis feitos por intermédio de Sistema são responsabilidade de Usufrente."},{"Text": "Estes termos podem ser alterados a qualquer momento.","Items": [{"Text": "Em caso de alteração, Usufrente receberá comunicação por e-mail e terá 30 dias para aceite de novos termos;"},{"Text": "Caso o prazo expire e Usufrente não aceite os novos termos, o acesso a Sistema será interrompido e Usufrente receberá comunicação via e-mail;"},{"Text": "Passados 90 dias da primeira comunicação, caso não aceite os novos termos, dados de Usufrente serão removidos permanentemente de Sistema."}]}]}'
		from contract
		where version = '003000000000'
	union all
